using System.Collections.Generic;
using BTCE_Trader.Api;
using BTCE_Trader.Api.RequestQueue;
using BTCE_Trader.Api.Web;
using NUnit.Framework;
using Rhino.Mocks;

namespace BTCE_Trader.Tests.Api
{
    [TestFixture]
    public class DepthApiTests
    {

        private IWebRequestWrapper webRequestWrapperMock;
        private IRequestInputQueue requestInputQueueMock;
        private BtceTradeApi btceApi;

        [SetUp]
        public void SetUp()
        {
            webRequestWrapperMock = MockRepository.GenerateMock<IWebRequestWrapper>();
            requestInputQueueMock = MockRepository.GenerateMock<IRequestInputQueue>();
            btceApi = new BtceTradeApi(webRequestWrapperMock, requestInputQueueMock);
        }

        [Test]
        public void ShouldCallDepthUrlWithCorrectParameters_multiplePairs()
        {
            var pairs = new List<BtcePairEnum>();
            pairs.Add(BtcePairEnum.ltc_btc);
            pairs.Add(BtcePairEnum.btc_usd);
            pairs.Add(BtcePairEnum.ltc_usd);

            btceApi.GetMarketDepths(pairs);
            webRequestWrapperMock.AssertWasCalled(a => a.RequestV3("depth", "ltc_btc-btc_usd-ltc_usd"));
        }

        [Test]
        public void ShouldCallDepthUrlWithCorrectParameters_singlePair()
        {
            var pairs = new List<BtcePairEnum>();
            pairs.Add(BtcePairEnum.ltc_btc);

            btceApi.GetMarketDepths(pairs);
            webRequestWrapperMock.AssertWasCalled(a => a.RequestV3("depth", "ltc_btc"));
        }

